import React, { useState } from 'react';

function Register() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    password: ''
  });

  const { firstName, lastName, username, email, password } = formData;
  const navigate = useNavigate();  // Initialize useNavigate

  const onSubmit = async e => {
    e.preventDefault();
    const newUser = { firstName, lastName, username, email, password };
    try {
      const config = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newUser)
      };
      const response = await fetch('/api/auth/register', config);
      const data = await response.json();
      console.log(data);
      if (response.ok) {
        alert('Registration successful. You can now login.'); // Optional: provide a success message
        navigate('/'); // Navigate to the login page after registration
      } else {
        throw new Error(data.message || 'Failed to register');
      }
    } catch (err) {
      console.error('Registration Error:', err);
      alert(err.message); // Display error message to the user
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input type="text" name="firstName" value={firstName} onChange={onChange} required />
      <input type="text" name="lastName" value={lastName} onChange={onChange} required />
      <input type="text" name="username" value={username} onChange={onChange} required />
      <input type="email" name="email" value={email} onChange={onChange} required />
      <input type="password" name="password" value={password} onChange={onChange} required />
      <button type="submit">Register</button>
    </form>
  );
}

export default Register;
